mapper.insert:
insert into tbl_member (name, email, password, company, dept)
values (#{name},#{email},#{password},#{company},#{dept})

	select Last_Insert_ID();

mapper.update:
update tbl_member set name=#{name}, password=#{password} 

	, email = #{email}


	, company=#{company}


	, dept=#{dept}

where mno = #{mno}
